package com.ft.membership.spoorclient;import javax.servlet.http.HttpServletRequest;import java.util.Optional;import java.util.function.Function;public class DefaultSpoorParametersFactory extends SpoorParametersFactory<SpoorParameters>{    public DefaultSpoorParametersFactory(String apiKey, String appRootUrl, Function<HttpServletRequest, String> productFactory) {        super(apiKey, appRootUrl, productFactory);    }    public DefaultSpoorParametersFactory(String apiKey, String appRootUrl, String product) {        super(apiKey, appRootUrl, product);    }    public SpoorParameters fromRequest(HttpServletRequest request, Optional<String> rootId) {        return new SpoorParameters(                buildSpoorSystem(),                buildSpoorContextFromRequest(request, rootId),                buildSpoorDeviceFromRequest(request),                "page",                "view");    }}